app.post("/api/arrears-mgmt-ok", async (req, res) => {
  const {
    promised_date_v,
    rc_code_v,
    notes_v,
    feedbk_code_v,
    princ_acc_v,
    username_v,
    Total_AMT_v,
    BR_CODE_v,
    risk_v,
    DAYS_IN_ARR_v,
    response_v,
  } = req.body;

  try {
    const connection = await oracledb.getConnection({
      user: DB_USER,
      password: DB_PASSWORD,
      connectString: DB_CONNECTION_STRING,
    });

    await connection.execute(
      `BEGIN prc_arrears_management_ok(
        :promised_date_v,
        :rc_code_v,
        :notes_v, 
        :feedbk_code_v,
        :princ_acc_v, 
        :username_v,
        :Total_AMT_v,
        :BR_CODE_v,
        :risk_v,
        :DAYS_IN_ARR_v,  
        :response_v
        ); END;`,
      {
        promised_date_v: {
          val: promised_date_v,
          dir: oracledb.BIND_IN,
          maxSize: 50,
        },
        rc_code_v: {
          val: rc_code_v,
          dir: oracledb.BIND_IN,
          maxSize: 20,
        },
        notes_v: {
          val: notes_v,
          dir: oracledb.BIND_IN,
          maxSize: 200,
        },
        feedbk_code_v: {
          val: feedbk_code_v,
          dir: oracledb.BIND_IN,
          maxSize: 20,
        },
        princ_acc_v: {
          val: princ_acc_v,
          dir: oracledb.BIND_IN,
          maxSize: 20,
        },
        username_v: {
          val: username_v,
          dir: oracledb.BIND_IN,
          maxSize: 20,
        },
        Total_AMT_v: {
          val: Total_AMT_v,
          dir: oracledb.BIND_IN,
          maxSize: 20,
        },
        BR_CODE_v: {
          val: BR_CODE_v,
          dir: oracledb.BIND_IN,
          maxSize: 20,
        },
        risk_v: {
          val: risk_v,
          dir: oracledb.BIND_IN,
          maxSize: 20,
        },
        DAYS_IN_ARR_v: {
          val: DAYS_IN_ARR_v,
          dir: oracledb.BIND_IN,
          maxSize: 20,
        },
        response_v: {
          val: response_v,
          dir: oracledb.BIND_OUT,
          maxSize: 200,
        },
      },

      function (err, result) {
        if (err) {
          console.log(err);
          response = {
            responseCode: "999",
            responseMessage: "Internal Server Error",
          };
          res.send(response);
        }
        if (result) {
          // return res.send(result);
          if (result.outBinds.response_v?.includes("07526")) {
            const message = result.outBinds.response_v;
            response = {
              responseCode: "000",
              responseMessage: message,
            };
          } else {
            response = {
              responseCode: "998",
              responseMessage: result.outBinds.response_v,
            };
          }
          res.send(response);
        }
      }
    );
  } catch (error) {
    console.error("Error:", error);
    res.send({
      // error: error.toString();
      responseCode: `999`,
      responseMessage: "Internal server error",
    });
  }
});

