app.post("/api/add-guarantees-info", async (req, res) => {
  try {
    const con = await oracledb.getConnection({
      user: DB_USER,
      password: DB_PASSWORD,
      connectString: DB_CONNECTION_STRING,
    });

    const {
      loan_app_no,
      ben_member_id,
      guarantor_member_id,
      guarantee_amount,
      posted_by,
    } = req.body;

    const outBinds = {
      msg: { type: oracledb.STRING, dir: oracledb.BIND_OUT },
    };

    con.execute(
      "BEGIN BANKOWNER.prc_guarantees_info(:loan_app_no, :ben_member_id, :guarantor_member_id, :guarantee_amount, :posted_by, :msg); END;",
      {
        loan_app_no: {
          type: oracledb.STRING,
          dir: oracledb.BIND_IN,
          val: loan_app_no,
        },
        ben_member_id: {
          type: oracledb.STRING,
          dir: oracledb.BIND_IN,
          val: ben_member_id,
        },
        guarantor_member_id: {
          type: oracledb.STRING,
          dir: oracledb.BIND_IN,
          val: guarantor_member_id,
        },
        guarantee_amount: {
          type: oracledb.NUMBER,
          dir: oracledb.BIND_IN,
          val: guarantee_amount,
        },
        posted_by: {
          type: oracledb.STRING,
          dir: oracledb.BIND_IN,
          val: posted_by,
        },
        msg: {
          type: oracledb.STRING,
          dir: oracledb.BIND_OUT,
        },
      },
      { outFormat: oracledb.OUT_FORMAT_OBJECT },
      (err, result) => {
        if (err) {
          console.error(err);
          res.status(500).json({ error: "Something went wrong." });
          return;
        }

        if (result) {
          const response = {
            outBinds: result.outBinds,
            msg: result.outBinds.msg,
          };
          res.status(200).json(response);
        } else {
          res.status(400).json({
            responseCode: 400,
            responseMessage: "Something went wrong.",
          });
        }
      }
    );
  } catch (err) {
    console.error(err);
    res
      .status(500)
      .json({ error: "An error occurred while processing the request." });
  }
});

